var assert = require('assert');
var should = require('should');

describe('Authentication', function() {
	it('should return string if the environment variable TWITTER_CONSUMER_KEY exists', function() {
		(typeof process.env.TWITTER_CONSUMER_KEY).should.equal('string');
	});

	it('should return string if the environment variable TWITTER_CONSUMER_SECRET exists', function() {
		(typeof process.env.TWITTER_CONSUMER_SECRET).should.equal('string');
	});

	it('should return string if the environment variable TWITTER_ACCESS_TOKEN_KEY exists', function() {
		(typeof process.env.TWITTER_ACCESS_TOKEN_KEY).should.equal('string');
	});

	it('should return string if the environment variable TWITTER_ACCESS_TOKEN_SECRET exists', function() {
		(typeof process.env.TWITTER_ACCESS_TOKEN_SECRET).should.equal('string');
	});
});
